<?php get_header(); ?>		<!-- Content -->	<div class="container contentarea">		<div class="row">			<div class="column-content">				<div id="content" role="main">										<?php while ( have_posts() ) : the_post(); ?>											<article id="post-<?php the_ID(); ?>" <?php post_class(); ?> itemscope itemtype="http://schema.org/WebPage">							<header class="entry-header">								<h1 class="entry-title" itemprop="name"><?php the_title(); ?></h1>							</header>							<div class="entry-content" itemprop="description">								<?php the_content(); ?>								<div class="clearfix"></div>								<?php wp_link_pages( array('before' => '<div class="page-link"> <span> Pages: </span>', 'after' => '</div>')); ?>							</div>															<footer class="entry-meta">								<?php edit_post_link( __( 'Edit', 'newzeo' ), '<span class="edit-link">', '</span>' ); ?>							</footer>														</article><!-- #post-<?php the_ID(); ?> -->												<?php comments_template( '', true ); ?>										<?php endwhile; ?>							</div> <!-- #content -->			</div> <!-- .column-content -->						<div class="column-sidebar nomargin">				<?php get_sidebar(); ?>			</div>					</div>	</div>	<?php get_footer(); ?>